import React, { useEffect } from 'react';
import auth0 from "../lib/auth0";
import router from "next/router";
import {IoMdLogOut} from "react-icons/io";

const App =  (props) => {
  useEffect(() => {
    if (!props.isAuth) {
      router.push("/")
    }
  })
  if (!props.isAuth) {
    return null
  }
  return (
    <div>
      <div className="mt-5 flex flex-row-reverse items-center">
      <img src={props.user.picture} className="mx-2 w-10 rounded-full" alt="perfil"/>
      <h3 className="mr-1">{props.user.name}</h3>
      <a href="/api/logout"><IoMdLogOut color="red" size={22}/></a>
      </div>
      <pre>{JSON.stringify(props, null, 2)}</pre>
    </div>
  )
}

export default App;

export async function getServerSideProps({req, res}){
  const session = await auth0.getSession(req)
  if (session) {
    return {
      props: {    
        isAuth: true,
        user: session.user
      }
    }
  }
  return {
    props: {
      isAuth: false,
      user: {}
    }
  }
}
