import React, { useState } from 'react';
import { IoMdLogOut } from 'react-icons/io';
import auth0 from '../lib/auth0';

const CreateStatus = (props) => {
  const [dados, setDados] = useState({
    status: 'bem',
    coords: {
      lat: null,
      long: null,
    },
  });
  const getMyLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setDados((old) => {
          return {
            ...old,
            coords: {
              lat: position.coords.latitude,
              long: position.coords.longitude,
            },
          };
        });
      });
    }
  };
  const onStatusChange = (evt) => {
    const { value } = evt.target;
    setDados((old) => {
      return {
        ...old,
        status: value,
      };
    });
  };
  return (
    <div>
      <div className="mt-2 flex flex-row-reverse items-center">
        <img
          src={props.user.picture}
          className="mx-2 w-10 rounded-full"
          alt="perfil"
        />
        <h3 className="mr-1">{props.user.name}</h3>
        <a href="/api/logout">
          <IoMdLogOut color="red" size={22} />
        </a>
      </div>
      <h1>CreateStatus</h1>
      <label className="block card-danger">
        <input
          type="radio"
          name="status"
          value="bem"
          onClick={onStatusChange}
        />{' '}
        Estou bem e sem simtomas.
      </label>
      <label className="block">
        <input
          type="radio"
          name="status"
          value="gripe"
          onClick={onStatusChange}
        />{' '}
        Estou com sintomas de gtipe / resfriado.
      </label>
      <label className="block">
        <input
          type="radio"
          name="status"
          value="covid"
          onClick={onStatusChange}
        />{' '}
        Estou com sintomas da COVID
      </label>
      Sua posição atual: {JSON.stringify(dados)}
      <button type="submit" onClick={getMyLocation}>
        Get my location
      </button>
    </div>
  );
};

export default CreateStatus;

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);
  if (session) {
    return {
      props: {
        isAuth: true,
        user: session.user,
      },
    };
  }
  return {
    props: {
      isAuth: false,
      user: {},
    },
  };
}
