import { db } from '../../lib/db';

const admin = require('firebase-admin');

const saveStatus = async (req, res) => {
  const dados = req.body;
  await db.collection('test').add({
    status: dados.status,
    coordinates: new admin.firestore.GeoPoint(
      dados.coords.lat,
      dados.coords.long
    ),
  });
  res.send({ ok: true });
};
export default saveStatus;
